mapper.select:
select
from t_customer where is_valid=1 and state=0
and name like concat('%',#{customerName},'%')
and khno =#{customerNo}
and level=#{level}

select
from t_customer c where is_valid=1 and state=0 and DATE_ADD(c.create_date,INTERVAL 6 MONTH) < NOW() and c.id not in( select distinct cus_id from t_customer_order o where is_valid=1 and o.state=1 and DATE_ADD(o.order_date,INTERVAL 6 MONTH) > NOW()
)
